environmentVariables.TOKENRELAY_CONFIG_JSON:
{  
  "proxy": {
    "auth": {
      "tokens": ["your-secure-proxy-token-here", "test-token-12345"],
      "encryptionKey": "change-this-32-character-key-here"
    },
    "mode": "direct",
    "timeoutSeconds": 300,
    "logBufferMinutes": 20,
    "permissions": {
      "allowTargetConfigOverride": false,
      "allowLogRetrieval": true,
      "allowLogLevelAdjustment": true
    },
    "targets": {
      "httpbin": {
        "endpoint": "https://httpbin.org",
        "healthCheckUrl": "https://httpbin.org/status/200",
        "description": "HTTPBin service for testing HTTP requests and responses",
        "headers": {
          "User-Agent": "TokenRelay/1.0.0",
          "Accept": "application/json"
        }
      },
      "jsonplaceholder": {
        "endpoint": "https://jsonplaceholder.typicode.com",
        "healthCheckUrl": "https://jsonplaceholder.typicode.com/posts/1",
        "description": "JSONPlaceholder API for testing",
        "headers": {
          "Accept": "application/json"
        }
      },
      "internal-api": {
        "endpoint": "https://internal-api.company.com",
        "healthCheckUrl": "https://internal-api.company.com/health",
        "description": "Internal company API with static auth",
        "authType": "static",
        "headers": {
          "Authorization": "Bearer api-key-here",
          "X-API-Version": "v1"
        }
      },
      "cerm": {
        "enabled": true,
        "endpoint": "https://your-server.example.com:43003",
        "healthCheckUrl": "",
        "description": "CERM API with OAuth password grant (explicit token_endpoint)",
        "authType": "oauth",
        "ignoreCertificateValidation": true,
        "authData": {
          "token_endpoint": "https://your-server.example.com:43002/oauth/token",
          "grant_type": "password",
          "username": "your-username",
          "password": "your-password",
          "client_id": "your-client-id",
          "client_secret": "your-client-secret",
          "scope": "scope"
        }
      },
      "another-oauth-api": {
        "enabled": true,
        "endpoint": "https://api.example.com",
        "description": "OAuth API with dynamic token_endpoint (will use https://api.example.com/oauth/token)",
        "authType": "oauth",
        "authData": {
          "grant_type": "password",
          "username": "api-user",
          "password": "api-password",
          "client_id": "client-id",
          "client_secret": "client-secret"
        }
      },
      "custom-grant-type-api": {
        "enabled": true,
        "endpoint": "https://api.custom.com",
        "description": "OAuth API with custom grant_type_form override (config uses 'password' but form sends 'custom_password')",
        "authType": "oauth",
        "authData": {
          "grant_type": "password",
          "grant_type_form": "custom_password",
          "username": "api-user",
          "password": "api-password",
          "client_id": "client-id",
          "client_secret": "client-secret",
          "scope": "read write"
        }
      }
    }
  },
  "plugins": {
    "fileStorage": {
      "enabled": true,
      "settings": {
        "destinations.documents": "C:\\uploads\\documents",
        "destinations.images": "C:\\uploads\\images",
        "destinations.temp": "C:\\uploads\\temp"
      }
    }
  },
  "logging": {
    "level": "Information",
    "destinations": [
      "console"
    ]
  }
}
